import React, {useState} from 'react';

const QuantityCounter = (props) => {
	const [quantity, setQuantity] = useState(1);

	const countDown = () => {
		quantity > 1 ? setQuantity(quantity - 1): setQuantity(quantity - 0);
	};
	return(
			<div class="col-6">
				<p>QTY</p>
				<button class={quantity === 1 ? "notAvailable quantityBtn" : "available quantityBtn"} onClick={countDown}>-</button>
				<p class="quantityCount">{quantity}</p>
				<button class="quantityBtn" onClick={() => setQuantity(quantity + 1)}>+</button>
			</div>
	)
};
export default QuantityCounter;
