import React, {useState} from 'react';
import PriceLoad from './priceLoader';

const QuantityCounter = (props) => {
	const [quantity, setQuantity] = useState(1);
	const [cart, setCart] = useState([]);

	const countDown = () => {
		quantity > 1 ? setQuantity(quantity - 1): setQuantity(quantity - 0);
	};

	const addToCart = () =>{
		for(let i = 0; i < quantity; i++){
			setCart((cart) => [...cart, props.product]);
		}
		quantity > 1 ? alert(quantity +" "+ props.product.name + "s added to cart") : alert(quantity +" "+ props.product.name + " added to cart");
	}

	return(
		<div className="row priceQuantity">
			<PriceLoad price={props.product.price}/>
			<div className="col-6 mb-cons">
				<div className="d-flex justify-content-end">
					<button className={quantity === 1 ? "notAvailable quantityBtn" : "available quantityBtn"} onClick={countDown} aria-label="decrease quantity">-</button>
					<p className="quantityCount align-self-center">{quantity}</p>
					<button className="quantityBtn" onClick={() => setQuantity(quantity + 1)} aria-label="increase quantity">+</button>
				</div>
			</div>
			<div className="col-12 text-md-center">
				<button className="shop-btn" onClick={addToCart} aria-label={"Add " + quantity + " to Cart"}> Add To Cart </button>
			</div>
		</div>

	)
};
export default QuantityCounter;
