import React, {useState} from 'react';
import PriceLoad from './components/priceLoader';

const QuantityCounter = (props) => {
	const [quantity, setQuantity] = useState(1);
	const [basket, setBasket] = useState([]);

	const countDown = () => {
		quantity > 1 ? setQuantity(quantity - 1): setQuantity(quantity - 0);
	};

	const addToBasket = () =>{
		for(let i = 0; i < quantity; i++){
			setBasket.push(props.product);
			alert('item added');
		}
	}

	return(
		<div class="row priceQuantity">
			<PriceLoad />
			<div class="col-6">
				<div class="d-flex justify-content-end">
					<button class={quantity === 1 ? "notAvailable quantityBtn" : "available quantityBtn"} onClick={countDown}>-</button>
					<p class="quantityCount">{quantity}</p>
					<button class="quantityBtn" onClick={() => setQuantity(quantity + 1)}>+</button>
				</div>
			</div>
			<div class="col-12">
				<button onClick={addToBasket}> Add To Cart </button>
			</div>
		</div>

	)
};
export default QuantityCounter;
