import React from 'react';

const PriceLoad = (props) =>{
	const priceSplit = () =>{
		const pounds = Array.from(props.price);
		const pence = pounds.splice(-2);
		return(
			<p class="price">pounds + "." + <span>pence</span></p>
		)
	}
	return(
		<div class="col-6 mb-cons">
			{priceSplit}
		</div>
	)
};
export default PriceLoad;
