import React, {Component} from 'react';


class App extends Component{
	constructor(props){
		super(props);
		this.state = {
	      error: null,
	      isLoaded: false,
	      items: []
	    };
	}
	//Lifecycle hook :https://reactjs.org/docs/faq-ajax.html
	componentDidMount() {
	    fetch("https://swapi.dev/api/people/1/")
	      .then(res => res.json())
	      .then(
	        (result) => {
	          this.setState({
	            isLoaded: true,
	            items: result.items
	          });
	        },
	        // Note: it's important to handle errors here
	        // instead of a catch() block so that we don't swallow
	        // exceptions from actual bugs in components.
	        (error) => {
	          this.setState({
	            isLoaded: true,
	            error
	          });
	        }
	      )
		}

	render() {
		const { error, isLoaded, items } = this.state;
	    return (
	    	<div>
		       <h1>Hello World</h1>
		       <ul>
		          {items.map(item => (
		            <li key={item.name}>
		              {item.name} {item.mass}
		            </li>
		          ))}
        		</ul>
		    </div>

	    )
	};
}

export default App;
