import React, {useState} from 'react';


const QuantityCounter = (props) => {
	const [quantity, setQuantity] = useState(1);

	const countUp = () =>{
		quantity++
	}
	const countDown = () => {
		quantity > 1 ? setQuantity(quantity--) : setQuantity(quantity);
	}
	return(
			<div>
				<button  class="minus" onClick={countDown}>-</button>
				<p>{this.props.quantity}</p>
				<button  class="minus" onClick={countUp}>+</button>
			</div>
	)
}
export default QuantityCounter;
