import React from 'react';

const PriceLoad = (props) =>{
	const priceSplit = (price) =>{
		const pounds = Array.from(price);
		const pence = pounds.splice(-2);
		console.log(pounds);
		console.log(pence);

		return <p className="price">{this.pounds} . <span>{this.pence}</span></p>
	};
	return(
		<div className="col-6 mb-cons">
			{priceSplit(props.price)}
		</div>
	)
};
export default PriceLoad;
