import React, {Component} from 'react';


class App extends Component{
	constructor(props){
		super(props);
		this.state = {
	      error: null,
	      isLoaded: false,
	      items: []
	    };
	}
	//Lifecycle hook :https://reactjs.org/docs/faq-ajax.html
	componentDidMount() {
	    fetch("http://127.0.0.1:8000/graphql#operationName=getProductById&query=query%20getProductById%20%7B%0A%20%20product(productId%3A%201)%20%7B%0A%20%20%20%20id%0A%20%20%20%20name%0A%20%20%20%20power%0A%20%20%20%20description%0A%20%20%20%20price%0A%20%20%20%20quantity%0A%20%20%20%20brand%0A%20%20%20%20weight%0A%20%20%20%20height%0A%20%20%20%20width%0A%20%20%20%20length%0A%20%20%20%20modelCode%0A%20%20%20%20colour%0A%20%20%20%20imgUrl%0A%20%20%7D%0A%7D%0A")
	      .then(res => res.json())
	      .then(
	        (result) => {
	          this.setState({
	            isLoaded: true,
	            items: result.items
	          });
	        },
	        // Note: it's important to handle errors here
	        // instead of a catch() block so that we don't swallow
	        // exceptions from actual bugs in components.
	        (error) => {
	          this.setState({
	            isLoaded: true,
	            error
	          });
	        }
	      )
		}

	render() {
		const { error, isLoaded, items } = this.state;
	    return (
	    	<div>
		       <h1>Hello World</h1>
		       {{items}}
		    </div>

	    )
	};
}

export default App;
